export const toc = [{
  value: 'Available Commands',
  id: 'available-commands',
  children: []
}, {
  value: 'Examples',
  id: 'examples',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The bank module is responsible for handling multi-asset coin transfers between accounts and tracking special-case
pseudo-transfers which must work differently with particular kinds of accounts (notably delegating/undelegating for vesting accounts).
It exposes several interfaces with varying capabilities for secure interaction with other modules which must alter user balances.`}</p>
    <p>{`In addition, the bank module tracks and provides query support for the total supply of all assets used in the application.`}</p>
    <h2 {...{
      "id": "available-commands"
    }}>{`Available Commands`}</h2>
    <hr></hr>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Command`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`total`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`total`}</inlineCode>{` command queries the total supply of tokens on chain.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`balances`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`balances`}</inlineCode>{` command queries a specific account balance by address`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`send`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`send`}</inlineCode>{` command creates and/or signs and broadcasts a MsgSend transaction.`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "examples"
    }}>{`Examples`}</h2>
    <hr></hr>
    <h4 {...{
      "id": "iond-query-bank-total"
    }}>{`iond query bank total`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond query bank total
`}</code></pre>
    <h4 {...{
      "id": "iond-query-bank-balances-address"
    }}>{`iond query bank balances `}{`[address]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond query bank balances idep1jg3j2daytay2qgf6d3aftdxjwqzhe655z485c2
`}</code></pre>
    <h4 {...{
      "id": "iond-tx-bank-send-from-to-ammount"
    }}>{`iond tx bank send `}{`[from]`}<em parentName="h4">{` `}{`[to]`}</em>{` `}{`[ammount]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond tx bank send [from_key_or_address] [to_address] [amount] [flags]
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
