  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Keys'
};
export const metadata = {
  "unversionedId": "Client/Keys",
  "id": "Client/Keys",
  "isDocsHomePage": false,
  "title": "Keys",
  "description": "Keys allows you to manage your local tendermint keystore (wallets) for iond.",
  "source": "@site/docs/Client/Keys.md",
  "slug": "/Client/Keys",
  "permalink": "/Client/Keys",
  "editUrl": "https://github.com/IDEP-network/IDEP-Docs/docs/Client/Keys.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Bank",
    "permalink": "/Client/Bank"
  },
  "next": {
    "title": "Tendermint",
    "permalink": "/Client/Tendermint"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Available Commands',
  id: 'available-commands',
  children: []
}, {
  value: 'Examples',
  id: 'examples',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Keys allows you to manage your local tendermint keystore (wallets) for iond.`}</p>
    <h2 {...{
      "id": "available-commands"
    }}>{`Available Commands`}</h2>
    <hr></hr>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Command`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`list`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`list`}</inlineCode>{` command lits all the keys stored by this key manager along with their associated name, type, address and pubkey.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`add`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`add`}</inlineCode>{` command lets add an encrypted private key, encrypt it, and save to disk.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`delete`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`delete`}</inlineCode>{` deletes the given key.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`import`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`import`}</inlineCode>{` command imports private keys into the local keybase.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`export`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`export`}</inlineCode>{` command exports private keys.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`mnemonic`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`mnemonic`}</inlineCode>{` command computes the bip39 mnemonic for some input entropy.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`parse`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`parse`}</inlineCode>{` command parses address from hex to bech32 and vice versa.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`show`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show`}</inlineCode>{` command retrieves key information by name or address.`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "examples"
    }}>{`Examples`}</h2>
    <hr></hr>
    <h4 {...{
      "id": "iond-keys-list-flags"
    }}>{`iond keys list `}{`[flags]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys list
`}</code></pre>
    <h4 {...{
      "id": "iond-keys-add-flags"
    }}>{`iond keys add `}{`[flags]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys add
`}</code></pre>
    <p>{`You must enter a password that is atleast 8 characters long.`}</p>
    <div {...{
      "className": "admonition admonition-caution alert alert--warning"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "16",
              "height": "16",
              "viewBox": "0 0 16 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M8.893 1.5c-.183-.31-.52-.5-.887-.5s-.703.19-.886.5L.138 13.499a.98.98 0 0 0 0 1.001c.193.31.53.501.886.501h13.964c.367 0 .704-.19.877-.5a1.03 1.03 0 0 0 .01-1.002L8.893 1.5zm.133 11.497H6.987v-2.003h2.039v2.003zm0-3.004H6.987V5.987h2.039v4.006z"
              }}></path></svg></span>{`caution`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Write down your mnemonic in a safe place! It is the only way to recover your account if you ever forget your password!`}</p></div></div>
    <h4 {...{
      "id": "iond-keys-delete-key-name-flags"
    }}>{`iond keys delete `}<em parentName="h4">{`key-name`}</em>{` `}{`[flags]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys delete mainkey
`}</code></pre>
    <h4 {...{
      "id": "iond-keys-import-keyname-keyfile-flags"
    }}>{`iond keys import `}<em parentName="h4">{`keyname`}</em>{` `}<em parentName="h4">{`keyfile`}</em>{` `}{`[flags]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys import mainkey examplefile 
`}</code></pre>
    <h4 {...{
      "id": "iond-keys-export-keyname-flags"
    }}>{`iond keys export `}<em parentName="h4">{`keyname`}</em>{` `}{`[flags]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys export mainkey [flags]
`}</code></pre>
    <pre><code parentName="pre" {...{}}>{`iond keys export mainkey --output-file=<path-to-keystore>
`}</code></pre>
    <h4 {...{
      "id": "iond-keys-mnemonic-flags"
    }}>{`iond keys mnemonic `}{`[flags]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys mnemonic [flags]
`}</code></pre>
    <p>{`To pass your own entropy, use unsafe-entropy mode.`}</p>
    <pre><code parentName="pre" {...{}}>{`iond keys mnemonic --unsafe-entropy
`}</code></pre>
    <h4 {...{
      "id": "iond-keys-parse-hex-or-bech32-address"
    }}>{`iond keys parse `}{`[hex-or-bech32-address]`}</h4>
    <pre><code parentName="pre" {...{}}>{`iond keys parse <hex-or-bech32-address> [flags]
`}</code></pre>
    <h4 {...{
      "id": "iond-keys-show-key-name-flags"
    }}>{`iond keys show `}<em parentName="h4">{`key-name`}</em>{` `}{`[flags]`}</h4>
    <p>{`flags`}</p>
    <hr></hr>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Name`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--address`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Output the address only (overrides --output)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--bech`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The Bech32 prefix encoding for a key (acc/val/cons)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--device`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Output the address in a ledger device`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--device`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`K out of N required signatures`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--pubkey`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Output the public key only (overrides --output)`}</td>
        </tr>
      </tbody>
    </table>
    <pre><code parentName="pre" {...{}}>{`iond keys show mainkey
`}</code></pre>
    <p>{`Get validator address.`}</p>
    <pre><code parentName="pre" {...{}}>{`iond keys show mainkey --bech val
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
