  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Validators Overview'
};
export const metadata = {
  "unversionedId": "ValidatorsOverview",
  "id": "ValidatorsOverview",
  "isDocsHomePage": false,
  "title": "Validators Overview",
  "description": "Introduction",
  "source": "@site/docs/ValidatorsOverview.md",
  "slug": "/ValidatorsOverview",
  "permalink": "/ValidatorsOverview",
  "editUrl": "https://github.com/IDEP-network/IDEP-Docs/docs/ValidatorsOverview.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Tx",
    "permalink": "/Client/Tx"
  },
  "next": {
    "title": "Installation",
    "permalink": "/Installation"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Introduction',
  id: 'introduction',
  children: []
}, {
  value: 'Hardware',
  id: 'hardware',
  children: []
}, {
  value: 'Community',
  id: 'community',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "introduction"
    }}>{`Introduction`}</h2>
    <p>{`The IDEP Network utilizes the `}<a parentName="p" {...{
        "href": "https://github.com/tendermint/tendermint"
      }}>{`Tendermint Core`}</a>{` consensus engine which relies
on a network of validators that are responsible for committing new blocks in the blockchain. These validators participate
in the consensus protocol by broadcasting votes which contain cryptographic signatures signed by each validator's private key.`}</p>
    <p>{`Validator candidates can bond their own IDEP tokens and have IDEP tokens "delegated", or staked, to them by token holders.
The IDEP Network will have a maximum total of 125 validators. The validators are determined by who has the most stake delegated
to them, the top 125 validators with the (most) highest number of staked tokens will become IDEP Network validators.`}</p>
    <p>{`Validators and their delegators will earn IDEP tokens as block provisions and tokens as transaction fees through execution
of the Tendermint consensus protocol. Note that validators can set commission on the fees their delegators receive as additional incentive.`}</p>
    <p>{`If validators double sign, are frequently offline or do not participate in governance, their staked IDEP tokens (including IDEP tokens of users that delegated to them) can be slashed. The penalty depends on the severity of the violation.`}</p>
    <h2 {...{
      "id": "hardware"
    }}>{`Hardware`}</h2>
    <p>{`Validators should expect to equip their datacenter location with redundant power, connectivity, and storage backups. Expect to have several redundant networking boxes for fiber, firewall and switching and then small servers with redundant hard drive and failover. Hardware can be on the low end of datacenter gear to start out with.`}</p>
    <p>{`We anticipate that network requirements will be low initially. The current testnet requires minimal resources. Then bandwidth, CPU and memory requirements will rise as the network grows. Large hard drives are recommended for storing years of blockchain history.`}</p>
    <p>{`Although there is no minimum system requirements you can use the following as a rough guide:`}</p>
    <ul>
      <li parentName="ul">{`Latest Version of Ubuntu`}</li>
      <li parentName="ul">{`4 CPU Processor`}</li>
      <li parentName="ul">{`8 GB Ram`}</li>
      <li parentName="ul">{`500GB Storage`}</li>
    </ul>
    <h2 {...{
      "id": "community"
    }}>{`Community`}</h2>
    <p>{`To get the latest news on becoming a validator please reach out to us on our community chat:`}</p>
    <p><a parentName="p" {...{
        "href": "https://discord.gg/Jrarctk4hG"
      }}>{`Discord`}</a>{` - Community discussions and support.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
