export const toc = [{
  value: 'Quick rundown',
  id: 'quick-rundown',
  children: []
}, {
  value: 'Validator-Setup',
  id: 'validator-setup',
  children: []
}, {
  value: 'FAQ',
  id: 'faq',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><img alt={"Denali"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!/home/robinzon/code/Oxeni/projects/IDEP-Docs/static/img/Denali1.png").default} /></p>
    <h2 {...{
      "id": "quick-rundown"
    }}>{`Quick rundown`}</h2>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`Make sure you have completed the `}<a parentName="p" {...{
            "href": "/Installation"
          }}>{`installation`}</a>{` successfully.`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Initialize your node with your moniker`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Copy the genesis file from the IDEP repo and place it in the nodes config directory`}</p>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Start the node`}</p>
      </li>
    </ul>
    <pre><code parentName="pre" {...{}}>{`iond init <moniker> --chain-id Test-Denali

iond keys add <accountname>

wget https://raw.githubusercontent.com/IDEP-network/testnet-binaries/main/Denali-0.0.1/genesis.json?token=APTYVG5VFPHLIWZB6NPQSIDBDSF7O

mv genesis.json ~/ion/config/

iond start --p2p.persistent_peers=95a7b71ab6ad8fad5f1ed3b49472683adea92cf1@142.93.65.220:26656,dc07da4be6ff285a1be2e9501fa92efef248d025@64.225.75.108:26656
`}</code></pre>
    <h2 {...{
      "id": "validator-setup"
    }}>{`Validator-Setup`}</h2>
    <p>{`Once the Full Node is up and running. Please share your IDEP address with the IDEP team. We will fund your address.`}</p>
    <pre><code parentName="pre" {...{}}>{`You can @ or DM denny007#3282 on Discord.
`}</code></pre>
    <p>{`To get your Public Address`}</p>
    <pre><code parentName="pre" {...{}}>{`iond keys list
`}</code></pre>
    <p>{`To get the Validator-pub-key`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tendermint show-validator
`}</code></pre>
    <p>{`Command to create a Validator`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx staking create-validator --amount <amount> --from <from-address> --pubkey <validator-pub-key> --commission-rate 0.01 --commission-max-rate 0.05 --commission-max-change-rate 0.005 --min-self-delegation 1 --chain-id Test-Denali
`}</code></pre>
    <h2 {...{
      "id": "faq"
    }}>{`FAQ`}</h2>
    <p>{`Example of a command to create a Validator`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx staking create-validator --amount 15000000000000idep --from idep1d2nqcwf9zz9fx7xlesyt0gc3utfxe2mk6nfwey --pubkey idepvalconspub1zcjduepqztw5yzm5wj0yc600aaemxlmda5488jv9hycxfnta3w7vz9jgpawqc9qnhs --commission-rate 0.01 --commission-max-rate 0.05 --commission-max-change-rate 0.005 --min-self-delegation 1 --chain-id Test-Denali
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
