export const toc = [{
  value: 'Available Commands',
  id: 'available-commands',
  children: []
}, {
  value: 'Examples',
  id: 'examples',
  children: [{
    value: 'Shows this node&#39;s Tendermint validator consensus address.',
    id: 'shows-this-nodes-tendermint-validator-consensus-address',
    children: []
  }, {
    value: 'iond tendermint show-node-id',
    id: 'iond-tendermint-show-node-id',
    children: []
  }, {
    value: 'iond tendermint show-validator',
    id: 'iond-tendermint-show-validator',
    children: []
  }, {
    value: 'iond tendermint version',
    id: 'iond-tendermint-version',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Tendermint consensus engine state querying commands.`}</p>
    <h2 {...{
      "id": "available-commands"
    }}>{`Available Commands`}</h2>
    <hr></hr>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Command`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`show-address`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show-address`}</inlineCode>{` command shows this node's tendermint validator consensus address.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`show-node-id`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show-node-id`}</inlineCode>{` command shows this node's ID.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`show-validator`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show-validator`}</inlineCode>{` command show this node's tendermint validator info.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`version`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`version`}</inlineCode>{` command prints tendermint libraries' version.`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "examples"
    }}>{`Examples`}</h2>
    <hr></hr>
    <h3 {...{
      "id": "shows-this-nodes-tendermint-validator-consensus-address"
    }}>{`Shows this node's Tendermint validator consensus address.`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tendermint show-address
`}</code></pre>
    <h3 {...{
      "id": "iond-tendermint-show-node-id"
    }}>{`iond tendermint show-node-id`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tendermint show-node-id
`}</code></pre>
    <h3 {...{
      "id": "iond-tendermint-show-validator"
    }}>{`iond tendermint show-validator`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tendermint show-validator
`}</code></pre>
    <h3 {...{
      "id": "iond-tendermint-version"
    }}>{`iond tendermint version`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tendermint version
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
