export const toc = [{
  value: 'Helpful commands for validators regarding keys',
  id: 'helpful-commands-for-validators-regarding-keys',
  children: []
}, {
  value: 'Account:',
  id: 'account',
  children: [{
    value: 'Generate new keys',
    id: 'generate-new-keys',
    children: []
  }, {
    value: 'Show all your available keys:',
    id: 'show-all-your-available-keys',
    children: []
  }, {
    value: 'Recover keys with mnemonic seed phrase:',
    id: 'recover-keys-with-mnemonic-seed-phrase',
    children: []
  }]
}, {
  value: 'Validator:',
  id: 'validator',
  children: [{
    value: 'Generate new validator keys:',
    id: 'generate-new-validator-keys',
    children: []
  }, {
    value: 'Show your nodes validator consensus address:',
    id: 'show-your-nodes-validator-consensus-address',
    children: []
  }, {
    value: 'Show your nodes validator pubkey:',
    id: 'show-your-nodes-validator-pubkey',
    children: []
  }, {
    value: 'Show your nodes validator <em>operators</em> address:',
    id: 'show-your-nodes-validator-operators-address',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "helpful-commands-for-validators-regarding-keys"
    }}>{`Helpful commands for validators regarding keys`}</h2>
    <br />
    <h2 {...{
      "id": "account"
    }}>{`Account:`}</h2>
    <h3 {...{
      "id": "generate-new-keys"
    }}>{`Generate new keys`}</h3>
    <p>{`You need an account private and public key pair to be able to receive funds, send txs, bond tx, etc.`}</p>
    <pre><code parentName="pre" {...{}}>{`iond keys add <account_name>
`}</code></pre>
    <h3 {...{
      "id": "show-all-your-available-keys"
    }}>{`Show all your available keys:`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond keys list
`}</code></pre>
    <h3 {...{
      "id": "recover-keys-with-mnemonic-seed-phrase"
    }}>{`Recover keys with mnemonic seed phrase:`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond keys add <account_name> --recover
`}</code></pre>
    <h2 {...{
      "id": "validator"
    }}>{`Validator:`}</h2>
    <h3 {...{
      "id": "generate-new-validator-keys"
    }}>{`Generate new validator keys:`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tx staking create-validator --amount <amount> --from <accAddress> --pubkey $(iond tendermint show-validator) --commission-rate <rate> --commission-max-rate <rate> --commission-max-change-rate <rate> --min-self-delegation <min-self-delegation-amount> --chain-id Test-Denali
`}</code></pre>
    <h3 {...{
      "id": "show-your-nodes-validator-consensus-address"
    }}>{`Show your nodes validator consensus address:`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tendermint show-address
`}</code></pre>
    <h3 {...{
      "id": "show-your-nodes-validator-pubkey"
    }}>{`Show your nodes validator pubkey:`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tendermint show-validator
`}</code></pre>
    <h3 {...{
      "id": "show-your-nodes-validator-operators-address"
    }}>{`Show your nodes validator `}<em parentName="h3">{`operators`}</em>{` address:`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond keys show <key address> --bech val
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
