  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'High-level Overview'
};
export const metadata = {
  "unversionedId": "Overview",
  "id": "Overview",
  "isDocsHomePage": false,
  "title": "High-level Overview",
  "description": "OverviewBanner",
  "source": "@site/docs/Overview.md",
  "slug": "/Overview",
  "permalink": "/Overview",
  "editUrl": "https://github.com/IDEP-network/IDEP-Docs/docs/Overview.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Welcome",
    "permalink": "/"
  },
  "next": {
    "title": "IDEP Litepaper",
    "permalink": "/Litepaper"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'What is IDEP',
  id: 'what-is-idep',
  children: [{
    value: 'Features',
    id: 'features',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><img alt={"OverviewBanner"} src={require("!url-loader?limit=10000&name=assets/images/[name]-[hash].[ext]&fallback=file-loader!/home/robinzon/code/Oxeni/projects/IDEP-Docs/static/img/OverviewBanner.png").default} /></p>
    <h2 {...{
      "id": "what-is-idep"
    }}>{`What is IDEP`}</h2>
    <p>{`IDEP is a scalable Proof-of-Stake blockchain built using `}<a parentName="p" {...{
        "href": "https://github.com/cosmos/cosmos-sdk"
      }}>{`Cosmos SDK`}</a>{` which utilizes the `}<a parentName="p" {...{
        "href": "https://github.com/tendermint/tendermint"
      }}>{`Tendermint Core`}</a>{` consensus engine.`}</p>
    <p>{`IDEP's core vision is to become the first global scale, digital media, AR platform utilizing the Tendermint's PoS
implementation. IDEP Network makes this possible by creating a geo-location based AR layer that is readily integrable
with mobile devices and smart glasses. You can think of this layer as a new standard in augmented reality experiences
as well as a complimentary implementation of networks blockchain. IDEP Network core modules will have full integration
and use of this AR layer. Also, because it is so tightly integrated with the network, it will be able to be used by the rest of the Cosmos Ecosystem and broader blockchain community. The Inter Blockchain Communication Protocol (IBC) will give rise to exciting collaborations and use cases underpinned with IDEP's AR tech. IDEP Network adopts the open source philosophy, inviting the entire IDEP community to contribute to the growth of the network, creating a self-sustaining decentralized platform.`}</p>
    <h3 {...{
      "id": "features"
    }}>{`Features`}</h3>
    <p>{`Heres a quick look at some of the exciting features of IDEP:`}</p>
    <ul>
      <li parentName="ul">{`Native Location-based Augmented Reality via Senary App (Coming Soon)`}</li>
      <li parentName="ul">{`AR focused cross-platform universal digital asset `}<a parentName="li" {...{
          "href": "https://github.com/IDEP-network/Galactic-Standards/blob/main/GS1.md"
        }}>{`standards`}</a></li>
      <li parentName="ul">{`High throughput using Tendermint Core`}</li>
      <li parentName="ul">{`Horizontal scalability enabled with `}<a parentName="li" {...{
          "href": "https://ibcprotocol.org"
        }}>{`IBC`}</a></li>
    </ul>
    <p>{`IDEP enables these key features by:`}</p>
    <ul>
      <li parentName="ul">{`Exposing a fully interoperable Augmented Reality layer for interacting with existing and future NFT and digital asset projects.`}</li>
      <li parentName="ul">{`Implementing Tendermint Core's Application Blockchain Interface `}<a parentName="li" {...{
          "href": "https://docs.tendermint.com/master/spec/abci/"
        }}>{`(ABCI)`}</a>{` to interact with the blockchain.`}</li>
      <li parentName="ul">{`Leveraging the modules concept and other mechanisms implemented by the Cosmos SDK.`}</li>
      <li parentName="ul">{`Utilizing native EVM and WASM modules to quickly onboard dapp developers.`}</li>
    </ul>
    <p>{`These exciting features will allow IDEP to revolutionize how we build the next generation of Augmented Reality
experiences and pave way for a new and exciting Cosmos ecosystem's future.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
