  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Tx'
};
export const metadata = {
  "unversionedId": "Client/Tx",
  "id": "Client/Tx",
  "isDocsHomePage": false,
  "title": "Tx",
  "description": "Tx allows you to sign or broadcast transactions",
  "source": "@site/docs/Client/Tx.md",
  "slug": "/Client/Tx",
  "permalink": "/Client/Tx",
  "editUrl": "https://github.com/IDEP-network/IDEP-Docs/docs/Client/Tx.md",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Tendermint",
    "permalink": "/Client/Tendermint"
  },
  "next": {
    "title": "Validators Overview",
    "permalink": "/ValidatorsOverview"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Available Commands',
  id: 'available-commands',
  children: [{
    value: 'iond query tx <em>hash</em>',
    id: 'iond-query-tx-hash',
    children: []
  }, {
    value: 'iond query txs',
    id: 'iond-query-txs',
    children: []
  }, {
    value: 'iond tx sign flags',
    id: 'iond-tx-sign-flags',
    children: []
  }, {
    value: 'iond tx broadcast',
    id: 'iond-tx-broadcast',
    children: []
  }, {
    value: 'iond tx multisign',
    id: 'iond-tx-multisign',
    children: []
  }, {
    value: 'Sign the multisig tx',
    id: 'sign-the-multisig-tx',
    children: []
  }]
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Tx allows you to sign or broadcast transactions`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Command`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`tx`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show-address`}</inlineCode>{` command queries a transaction by hash in a block that's been committed.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`txs`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show-node-id`}</inlineCode>{` command searches for transactions that match the exact given events where results are paginated.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`sign`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`show-validator`}</inlineCode>{` command signs transactions that are generated offline.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`broadcast`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`broadcast`}</inlineCode>{` command broadcasts a signed transaction to the IDEP network.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`multisign`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The `}<inlineCode parentName="td">{`multisign`}</inlineCode>{` command signs the same transaction by multiple accounts.`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "available-commands"
    }}>{`Available Commands`}</h2>
    <hr></hr>
    <h3 {...{
      "id": "iond-query-tx-hash"
    }}>{`iond query tx `}<em parentName="h3">{`hash`}</em></h3>
    <pre><code parentName="pre" {...{}}>{`iond query tx 6352278EA34E6CAD7A536BB36A9B12934C6DEEAAAF6D6356522998EF0758A6E2
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "iond-query-txs"
    }}>{`iond query txs`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond query txs --events 'message.sender=<iaa...>&message.action=xxxx' --page 1 --limit 30
`}</code></pre>
    <h3 {...{
      "id": "iond-tx-sign-flags"
    }}>{`iond tx sign `}{`[flags]`}</h3>
    <p>{`Sign transactions in generated offline file. The file created with the --generate-only flag.`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx sign <file> [flags]
`}</code></pre>
    <p>{`Flags`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Name`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Required`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--append`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`bool`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Attach a signature to an existing signature.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--from`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`bool`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Key name for signature.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--offline`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`bool`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Offline mode.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--signature-only`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`bool`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Print only the generated signature, then exit.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`--multisig`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`bool`}</td>
          <td parentName="tr" {...{
            "align": null
          }}></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`true`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Address of the multisig account on behalf of which the transaction shall be signed.`}</td>
        </tr>
      </tbody>
    </table>
    <hr></hr>
    <h3 {...{
      "id": "iond-tx-broadcast"
    }}>{`iond tx broadcast`}</h3>
    <pre><code parentName="pre" {...{}}>{`iond tx broadcast signed.json --chain-id=Test-Denali
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "iond-tx-multisign"
    }}>{`iond tx multisign`}</h3>
    <p>{`Sign a transaction by multiple accounts. The tx could be broadcasted only when the number of signatures meets the multisig-threshold.`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx multisign <file> <key-name> <[signature]...> [flags]
`}</code></pre>
    <p>{`Generate an offline tx by multisig key`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx bank send <from> <to> 69idep --fees=500idep --chain-id=Test-Denali --from=<multisig-keyname> --generate-only > unsigned.json
`}</code></pre>
    <h3 {...{
      "id": "sign-the-multisig-tx"
    }}>{`Sign the multisig tx`}</h3>
    <p>{`Query the multisig address`}</p>
    <pre><code parentName="pre" {...{}}>{`iond keys show <multisig-keyname>
`}</code></pre>
    <h5 {...{
      "id": "sign-the-unsignedjson"
    }}>{`Sign the `}<inlineCode parentName="h5">{`unsigned.json`}</inlineCode></h5>
    <p>{`Assume the multisig-threshold is 2, here we sign the unsigned.json by 2 of the signers`}</p>
    <p>{`Sign the tx by signer-1:`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx sign unsigned.json --from=<signer-keyname-1> --chain-id=Test-Denali --multisig=<multisig-address> --signature-only > signed-1.json
`}</code></pre>
    <p>{`Sign the tx by signer-2:`}</p>
    <pre><code parentName="pre" {...{}}>{`iond tx sign unsigned.json --from=<signer-keyname-2> --chain-id=Test-Denali --multisig=<multisig-address> --signature-only > signed-2.json
`}</code></pre>
    <h5 {...{
      "id": "merge-the-signatures"
    }}>{`Merge the signatures`}</h5>
    <p>{`Merge all the signatures into `}<inlineCode parentName="p">{`signed.json`}</inlineCode></p>
    <pre><code parentName="pre" {...{}}>{`iond tx multisign --chain-id=Test-Denali unsigned.json <multisig-keyname> signed-1.json signed-2.json > signed.json
`}</code></pre>
    <p>{`Now broadcast the tx the same was as you would a regular one.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
